#include <iostream>
#include <vector>

using namespace std;

class Solution {
public:
    int maximumScore(vector<int>& nums, vector<int>& multipliers) {
        int m = multipliers.size();
        int n = nums.size();
        // O(mn) space
        // vector<vector<int>> dp(m+1, vector<int>(n, 0));
        // for (int j = 0; j < m; j++) dp[m-1][j] = max(multipliers[m-1] * nums[j], multipliers[m-1] * nums[j+n-m]);
        // for (int i = m-2; i >= 0; i--){
        //     for (int j = 0; j <= i; j++){
        //         dp[i][j] = max(multipliers[i] * nums[j] + dp[i+1][j+1], multipliers[i] * nums[j+n-i-1] + dp[i+1][j]);
        //     }
        // }

        // optimize space O(mn) => O(n)
        vector<vector<int>> dp(2, vector<int>(n,0));
        for (int j = 0; j < m; j++) dp[(m-1)%2][j] = max(multipliers[m-1] * nums[j], multipliers[m-1] * nums[j+n-m]);
        for (int i = m-2; i >= 0; i--){
            for (int j = 0; j <= i; j++){
                dp[i%2][j] = max(multipliers[i] * nums[j] + dp[(i+1)%2][j+1], multipliers[i] * nums[j+n-i-1] + dp[(i+1)%2][j]);
            }
        }


        // cout << endl;
        // for (auto i : dp){
        //     for (auto j : i) cout << j << " ";
        //     cout << endl;
        // }

        return dp[0][0];
    }
};

