#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

class Solution {
public:
    int threeSumMulti(vector<int>& arr, int target) {
        int ans = 0;
        int module = 1000000007;
        vector<int> count(101,0);
        for (int i : arr) count[i]++;
        for (int i = 0; i <= min(target, 100); i++){
            for (int j = i; j <= min(target, 100); j++){
                int find = target - i - j;
                // cout << i << " " << j << " " << find << endl;
                if (find < j) break;
                if (find > 100) continue;
                double temp = count[i];
                count[i]--;
                temp *= count[j];
                count[j]--;
                temp *= count[find];
                if (i == j || j == find || i == find) temp /= 2;
                if (i == j && j == find) temp /= 3;
                ans += (int)fmod(temp, module);
                count[i]++;
                count[j]++;
                // cout << ans << endl;
            }
        }
        return ans;
    }
};

